package com.java.control.Statements;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 5;

		switch(a) {
		case 1:System.out.println("False");break;
		case 2:System.out.println("False");break;
		case 3:System.out.println("False");break;
		case 4:System.out.println("False");break;
		case 5:System.out.println("True");break;
		default:System.out.println("False");
		}
	}
}


/* String and Switch will be supported from 1.7 version. */